import unittest

from day_02_01 import get_next_number, calculate_code


class GetNextNumberTests(unittest.TestCase):

    def test_up_from_five_gets_2(self):
        result = get_next_number('U')
        self.assertEqual(2, result)

    def test_up_left_returns_1(self):
        result = get_next_number('UL')
        self.assertEqual(1, result)


